    if (lhs.m_endLine != rhs.m_endLine) {
      return lhs.m_endLine > rhs.m_endLine;
    }
    if (lhs.m_endColumn != rhs.m_endColumn) {
      return lhs.m_endColumn > rhs.m_endColumn;
    }
